def similar (path_file1,path_file2,split_at):
    key_word = ['the', 'is', 'are', 'we', 'have', 'a', ',', 'an', 'of', 'from', 'so', 'as', 'was', 'were', 'can',
                'been', 'in', 'has', 'by', 'such', 'and', 'be', 'We', 'where', 'to', 'it', 'In', 'for', 'with', 'also']

    my_file1 = open(path_file1, "r")
    content = my_file1.read()
    content_list1 = content.split(split_at)
    my_file1.close()

    my_file2 = open(path_file2, "r")
    content = my_file2.read()
    content_list2 = content.split(split_at)
    my_file2.close()
    s = set(key_word)
    temp1 = [x for x in content_list1 if x not in s]
    #   print('list_f', temp1)
    temp2 = [x for x in content_list2 if x not in s]
    #   print('list_f', temp2)
    similar_words = []
    for i in range(len(temp1)):
        z = temp1[i]
        for x in range(len(temp2)):
            w = temp2[x]
            if z == w:
                #     print(i)
                #     print(z)
                similar_words.append(z)
                break

    print('similar_words', similar_words)
    len_similar = len(similar_words)
    len_list1 = len(temp1)
    return len_similar, len_list1
